import { Component, OnInit } from '@angular/core';
import { Prestamo } from '../prestamo';
import { PrestamoService } from '../prestamo.service';
import { Router } from '@angular/router';
import swal from 'sweetalert2';

@Component({
  selector: 'app-lista-prestamos',
  templateUrl: './lista-prestamos.component.html',
  styleUrls: ['./lista-prestamos.component.css']
})
export class ListaPrestamosComponent implements OnInit {

  prestamos: Prestamo[];

  constructor(private prestamoServicio: PrestamoService, private router: Router) {
    this.prestamoServicio = prestamoServicio;
    this.router = router;
    this.prestamos = [];
  }

  ngOnInit(): void {
    this.obtenerPrestamos();
  }

  actualizarPrestamo(id: number) {
    return this.router.navigate(['actualizar-prestamo', id]);
  }

  private obtenerPrestamos() {
    this.prestamoServicio.obtenerListaDePrestamos().subscribe(dato => {
      this.prestamos = dato;
    });
  }

  eliminarPrestamo(id: number) {
    swal({
      title: '¿Estas seguro?',
      text: "Confirma si deseas eliminar el préstamo",
      type: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si , elimínalo',
      cancelButtonText: 'No, cancelar',
      confirmButtonClass: 'btn btn-success',
      cancelButtonClass: 'btn btn-danger',
      buttonsStyling: true
    }).then((result) => {
      if (result.value) {
        this.prestamoServicio.eliminarPrestamo(id).subscribe(dato => {
          console.log(dato);
          this.obtenerPrestamos();
          swal(
            'Préstamo eliminado',
            'El préstamo ha sido eliminado con éxito',
            'success'
          );
        })
      }
    })
  }

  verDetallesDelPrestamo(id: number) {
    this.router.navigate(['prestamo-detalles', id]);
  }

}
<h2>Lista de Mensajes</h2>
<table class="table table-striped">
    <thead class="table table-dark">
        <tr>
            <th>Nombre</th>
            <th>Comision</th>
            <th>Meses</th>
            <th>Fecha</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let prestamo of prestamos">
            <td>{{prestamo.nombre}}</td>
            <td>{{prestamo.comision}}</td>
            <td>{{prestamo.meses}}</td>
            <td>
                <button (click)="actualizarPrestamo(prestamo.id)" class="btn btn-info">Editar</button>
                <button (click)="eliminarPrestamo(prestamo.id)" class="btn btn-danger" style="margin-left: 10px">Eliminar</button>
                <button (click)="verDetallesDelPrestamo(prestamo.id)" class="btn btn-primary" style="margin-left: 10px">Detalles</button>
            </td>
        </tr>
    </tbody>
</table>
