import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { firstValueFrom } from 'rxjs';

@Component({
  selector: 'app-actualizar-cliente',
  templateUrl: './actualizar-cliente.component.html',
  styleUrls: ['./actualizar-cliente.component.css']
})
export class ActualizarClienteComponent implements OnInit{

  cliente : Cliente = new Cliente();

  constructor (private clienteServicio:ClienteService, private router:Router, private route:Ro) {

  }
  ngOnInit(): void {}

  async guardarCliente() {
    try {
      const dato = await firstValueFrom(this.clienteServicio.registrarCliente(this.cliente));
      console.log(dato);
      this.irALaListaDeClientes();
    } catch (error) {
      console.log(error);
    }
  }

  irALaListaDeClientes(){
    this.router.navigate(['/clientes']);
  }

  onSubmit() {
    this.guardarCliente();
  }
}
