import { Component, OnInit } from '@angular/core';
import { Transferencia } from '../transferencia';
import { TransferenciaService } from '../transferencia.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-actualizar-transferencia',
  templateUrl: './actualizar-transferencia.component.html',
  styleUrls: ['./actualizar-transferencia.component.css']
})
export class ActualizarTransferenciaComponent implements OnInit {

  id:number;
  transferencia:Transferencia = new Transferencia();

  constructor(private transferenciaServicio:TransferenciaService, private router:Router, private route:ActivatedRoute) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.transferenciaServicio.obtenerTransferenciaPorId(this.id).subscribe(dato => {
      this.transferencia = dato;
    }, error => console.log(error));
  }

  irALaListaDeTransferencias() {
    this.router.navigate(['/transferencias']);
  }

  onSubmit() {
    this.irALaListaDeTransferencias();
    this.transferenciaServicio.ac(this.id, this.mensaje).subscribe(dato => {
      this.irALaListaDeTransferencias();
    },error => console.log(error));
  }

}
<div class="col-md-6 offset-md-3">
    <br>
    <h3 class="text-center">Enviar Transferencia</h3>
    <br>
    <form (ngSubmit)="onSubmit()" #transferenciaForm="ngForm">
        <div class="form-group">
            <label>Id Ordenante: </label>
            <input type="number" class="form-control" id="transferencia" [(ngModel)]="transferencia.id_ordenante" name="id_ordenante" required
             placeholder="Indique el id del cliente que envia la transferencia" #id_ordenante="ngModel">
            <div class="alert alert-danger" *ngIf="id_ordenante.invalid && (id_ordenante.dirty || id_ordenante.touched)">
                <div *ngIf="id_ordenante.errors">
                    Indique el id del cliente que envia la transferencia
                </div>
                <div *ngIf="id_ordenante.errors">
                    Consulte la lista de clientes para saber los id
                </div>
            </div>
        </div>
        <br>
        <div class="form-group">
            <label>Id Beneficiario: </label>
            <input type="number" class="form-control" id="transferencia" [(ngModel)]="transferencia.id_beneficiario" name="id_beneficiario" required
             placeholder="Indique el id del cliente que recibe la transferencia" #id_beneficiario="ngModel">
            <div class="alert alert-danger" *ngIf="id_beneficiario.invalid && (id_beneficiario.dirty || id_beneficiario.touched)">
                <div *ngIf="id_beneficiario.errors">
                    Indique el id del cliente que recibe la transferencia
                </div>
                <div *ngIf="id_beneficiario.errors">
                    Consulte la lista de clientes para saber los id
                </div>
            </div>
        </div>
        <br>
        <div class="form-group">
            <label>Importe: </label>
            <input type="number" class="form-control" id="transferencia" [(ngModel)]="transferencia.importe" name="importe" required
                minlength="4" maxlength="200" placeholder="Escriba el mensaje" #importe="ngModel">
            <div class="alert alert-danger" *ngIf="importe.invalid && (importe.dirty || importe.touched)">
                <div *ngIf="importe.errors">
                    Escriba el importe de la transferencia
                </div>
            </div>
        </div>
        <br>
        <div class="form-group">
            <label>Concepto: </label>
            <input type="text" class="form-control" id="transferencia" [(ngModel)]="transferencia.concepto" name="concepto" required
                minlength="4" maxlength="20" placeholder="Escriba el mensaje" #concepto="ngModel">
            <div class="alert alert-danger" *ngIf="concepto.invalid && (concepto.dirty || concepto.touched)">
                <div *ngIf="concepto.errors">
                    Escriba el concepto de la transferencia con un mínimo de 4 cáracteres hasta un máximo de 20
                </div>
            </div>
        </div>
        <br>
        <button class="btn btn-success">Enviar</button>
    </form>
</div>
