import { Component, OnInit } from '@angular/core';
import { Cliente } from '../cliente';
import { ClienteService } from '../cliente.service';
import { Router } from '@angular/router';
import { firstValueFrom } from 'rxjs';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-registrar-cliente',
  templateUrl: './registrar-cliente.component.html',
  styleUrls: ['./registrar-cliente.component.css']
})
export class RegistrarClienteComponent implements OnInit {

  cliente : Cliente = new Cliente();
  constructor(private clienteServicio:ClienteService, private router:Router, private httpClient) {}

  ngOnInit(): void{ }


  async guardarCliente() {
    try {
      const dato = await firstValueFrom(this.clienteServicio.registrarCliente(this.cliente));
      console.log(dato);
      this.irALaListaDeClientes();
    } catch (error) {
      console.log(error);
    }
  }

  irALaListaDeClientes(){
    this.router.navigate(['/clientes']);
  }

  onSubmit() {
    this.guardarCliente();
  }
}
