import { Component, OnInit } from '@angular/core';
import { Transferencia } from '../transferencia';
import { Router } from '@angular/router';
import { TransferenciaService } from '../transferencia.service';
import swal from 'sweetalert2';

@Component({
  selector: 'app-lista-transferencias',
  templateUrl: './lista-transferencias.component.html',
  styleUrls: ['./lista-transferencias.component.css']
})
export class ListaTransferenciasComponent implements OnInit {

  transferencias:Transferencia[];

  constructor(private transferenciaServicio:TransferenciaService, private router:Router) {
    this.transferenciaServicio = transferenciaServicio;
    this.router = router;
    this.transferencias = [];
  }

  ngOnInit(): void {
    this.obtenerTransferencias();
  }

  actualizarMensaje(id:number) {
    return this.router.navigate(['actualizar-mensaje', id]);
  }

  private obtenerTransferencias() {
    this.transferenciaServicio.obtenerListaDeTransferencias().subscribe(dato => {
      this.transferencias = dato;
    });
  }

  eliminarMensaje(id:number) {
    swal({
      title: '¿Estas seguro?',
      text: "Confirma si deseas eliminar el mensaje",
      type: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si , elimínalo',
      cancelButtonText: 'No, cancelar',
      confirmButtonClass: 'btn btn-success',
      cancelButtonClass: 'btn btn-danger',
      buttonsStyling: true
    }).then((result) => {
      if (result.value) {
        this.mensajeServicio.eliminarMensaje(id).subscribe(dato => {
          console.log(dato);
          this.obtenerMensajes();
          swal(
            'Mensaje eliminado',
            'El mensaje ha sido eliminado con éxito',
            'success'
          );
        })
      }
    })
  }

  verDetallesDelMensaje(id:number) {
    this.router.navigate(['mensaje-detalles', id]);
  }


}
<h2>Lista de Transferencias</h2>
<table class="table table-striped">
    <thead class="table table-dark">
        <tr>
            <th>Id Ordenante</th>
            <th>Id Beneficiario</th>
            <th>Concepto</th>
            <th>Importe</th>
            <th>Fecha</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let transferencia of transferencias">
            <td>{{transferencia.id_origen}}</td>
            <td>{{transferencia.id_destino}}</td>
            <td>{{transferencia.texto}}</td>
            <td>{{transferencia.fecha}}</td>
