import { Component, OnInit } from '@angular/core';
import { Transferencia } from '../transferencia';

@Component({
  selector: 'app-actualizar-transferencia',
  templateUrl: './actualizar-transferencia.component.html',
  styleUrls: ['./actualizar-transferencia.component.css']
})
export class ActualizarTransferenciaComponent implements OnInit {

  id:number;
  transferencia:Transferencia = new Mensaje();

  constructor(private mensajeServicio:MensajeService, private router:Router, private route:ActivatedRoute) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.mensajeServicio.obtenerMensajePorId(this.id).subscribe(dato => {
      this.mensaje = dato;
    }, error => console.log(error));
  }

  irALaListaDeMensajes() {
    this.router.navigate(['/mensajes']);
  }

  onSubmit() {
    this.irALaListaDeMensajes();
    this.mensajeServicio.actualizarMensaje(this.id, this.mensaje).subscribe(dato => {
      this.irALaListaDeMensajes();
    },error => console.log(error));
  }

}
