import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-transferencia-detalles',
  templateUrl: './transferencia-detalles.component.html',
  styleUrls: ['./transferencia-detalles.component.css']
})
export class TransferenciaDetallesComponent implements OnInit {

  id:number;
  mensaje:Mensaje;

  constructor(private mensajeServicio:MensajeService, private route:ActivatedRoute) {}

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.mensaje =  new Mensaje();
    this.mensajeServicio.obtenerMensajePorId(this.id).subscribe(dato => {
      this.mensaje = dato;
      swal(`Detalles del mensaje ${this.mensaje.id}`)
    });
  }


}
