import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Cliente } from './cliente';

@Injectable({
  providedIn: 'root'
})
export class ClienteService {

  private baseUrl = "http://localhost:8080/bankapp/v1/clientes";

  constructor(private htppClient:HttpClient) { }

  obtenerListaDeClientes():Observable<Cliente[]> {
    return this.htppClient.get<Cliente[]>(`${this.baseUrl}`);
  }

  registrarCliente(cliente:Cliente) : Observable<Object> {
    return this.htppClient.post<Cliente[]>(`${this.baseUrl}`, cliente);
  }

  onSubmit(){
    this.htppClient.get("/bankapp/v2")
  }
}
