import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Cliente } from './cliente';
import { Gestor } from './gestor';

@Injectable({
  providedIn: 'root'
})
export class ClienteService {


  private baseUrl = "http://localhost:8080/bankapp/v1/clientes";

  constructor(private httpClient:HttpClient) { }

  obtenerListaDeClientes():Observable<Cliente[]> {
    return this.httpClient.get<Cliente[]>(`${this.baseUrl}`);
  }

  registrarCliente(cliente:Cliente) : Observable<Object> {
    return this.htppClient.post<Cliente[]>(`${this.baseUrl}`, cliente);
  }

  obtenerGestor(): Observable<Gestor> {
    return this.httpClient.get<Gestor>('http://localhost:8080/bankapp/v2/gestores/random');
  }
}
