import { Component, OnInit } from '@angular/core';
import { Mensaje } from '../mensaje';
import { MensajeService } from '../mensaje.service';
import { Router } from '@angular/router';
import swal from 'sweetalert2';

@Component({
  selector: 'app-lista-mensajes',
  templateUrl: './lista-mensajes.component.html',
  styleUrls: ['./lista-mensajes.component.css']
})
export class ListaMensajesComponent implements OnInit {

  mensajes:Mensaje[];

  constructor(private mensajeServicio:MensajeService, private router:Router) {
    this.mensajeServicio = mensajeServicio;
    this.router = router;
    this.mensajes = [];
  }

  ngOnInit(): void {
    this.obtenerMensajes();
  }

  actualizarMensaje(id:number) {
    return this.router.navigate(['actualizar-mensaje', id]);
  }

  private obtenerMensajes() {
    this.mensajeServicio.obtenerListaDeMensajes().subscribe(dato => {
      this.mensajes = dato;
    });
  }

  eliminarMensaje(id:number) {
    swal({
      title: '¿Estas seguro?',
      text: "Confirma si deseas eliminar el gestor",
      type: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si , elimínalo',
      cancelButtonText: 'No, cancelar',
      confirmButtonClass: 'btn btn-success',
      cancelButtonClass: 'btn btn-danger',
      buttonsStyling: true
    }).then((result) => {
      if (result.value) {
        this.gestorServicio.eliminarGestor(id).subscribe(dato => {
          console.log(dato);
          this.obtenerGestores();
          swal(
            'Gestor eliminado',
            'El gestor ha sido eliminado con éxito',
            'success'
          );
        })
      }
    })
  }

}
<h2>Lista de Mensajes</h2>
<table class="table table-striped">
    <thead class="table table-dark">
        <tr>
            <th>Id de Origen</th>
            <th>Id de Destino</th>
            <th>Texto</th>
            <th>Fecha</th>
            <th>Acciones</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let mensaje of mensajes">
            <td>{{mensaje.id_origen}}</td>
            <td>{{mensaje.id_destino}}</td>
            <td>{{mensaje.texto}}</td>
            <td>{{mensaje.fecha}}</td>
            <td>
                <button (click)="actualizarMensaje(mensaje.id)" class="btn btn-info">Editar</button>
                <button (click)="eliminarMensaje(mensaje.id)" class="btn btn-danger" style="margin-left: 7.5px">Eliminar</button>
                <!-- <button (click)="verDetallesDelGestor(mensaje.id)" class="btn btn-primary" style="margin-left: 7.5px">Detalles</button>
            </td> -->
        </tr>
    </tbody>
</table>
